@using Microsoft.CodeAnalysis
<h1>Old School</h1>
<div id="oldSchool">
    @ViewData["utcNow"]
</div>
<div id="oldSchoolIdentity">
</div>
@section scripts{
    <script>
        var myDiv = document.getElementById("oldSchoolIdentity");
        fetch('/api/old-school/IdentityApi', {
                credentials: 'include'
            })
            .then(function(response) {
                return response.text();
            }).then(function(text) {
                console.log(text);
                myDiv.innerHTML = text;
            });
    </script>
}
